import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import '../styles/App.css';

const Login = () => {
  const { login, ADMIN_USER } = useAuth();
  const navigate = useNavigate();
  const [form, setForm] = useState({ username: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((f) => ({ ...f, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    const res = await login(form.username.trim(), form.password.trim());
    setLoading(false);
    if (res.success) {
      navigate('/admin', { replace: true });
    } else {
      setError('Invalid username or password.');
    }
  };

  return (
    <main className="page-shell" style={{ maxWidth: 520 }}>
      <div className="hero-card" style={{ gap: 16 }}>
        <header>
          <div className="eyebrow">Kriedko Culinary Collective</div>
          <h1 style={{ margin: 0 }}>Admin Login</h1>
          <p className="muted">Use your admin credentials to continue.</p>
        </header>

        {error && (
          <div style={{
            color: 'var(--accent-deep)',
            background: 'rgba(177,71,60,0.1)',
            border: '1px solid rgba(177,71,60,0.2)',
            padding: '10px 12px',
            borderRadius: 10
          }}>{error}</div>
        )}

        <form onSubmit={handleSubmit} className="form-grid" style={{ gap: 14 }}>
          <label className="form-row">
            <span>Username</span>
            <input
              type="text"
              name="username"
              placeholder={ADMIN_USER}
              value={form.username}
              onChange={handleChange}
              required
            />
          </label>
          <label className="form-row">
            <span>Password</span>
            <input
              type="password"
              name="password"
              placeholder="••••••••"
              value={form.password}
              onChange={handleChange}
              required
            />
          </label>
          <div style={{ display: 'flex', gap: 10 }}>
            <button className="form-cta" type="submit" disabled={loading}>
              {loading ? 'Signing in…' : 'Sign In'}
            </button>
            <Link to="/" className="admin-link" style={{ position: 'static', boxShadow: 'none' }}>
              Back to Form
            </Link>
          </div>
        </form>
      </div>
    </main>
  );
};

export default Login;
