import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import '../styles/App.css';
import '../styles/Login.css';

const Login = () => {
  const { login, ADMIN_USER } = useAuth();
  const navigate = useNavigate();
  const [form, setForm] = useState({ username: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((f) => ({ ...f, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    const res = await login(form.username.trim(), form.password.trim());
    setLoading(false);
    if (res.success) {
      navigate('/admin', { replace: true });
    } else {
      setError('Invalid username or password.');
    }
  };

  return (
    <div className="login-container">
      {/* Background Elements */}
      <div className="login-bg">
        <div className="login-bg-circle login-bg-circle-1"></div>
        <div className="login-bg-circle login-bg-circle-2"></div>
        <div className="login-bg-circle login-bg-circle-3"></div>
      </div>

      {/* Main Content */}
      <div className="login-content">
        <div className="login-card">
          {/* Header */}
          <div className="login-header">
            <div className="login-logo">
              <div className="login-logo-icon">üçΩÔ∏è</div>
            </div>
            <div className="login-brand">
              <h1 className="login-title">Welcome Back</h1>
              <p className="login-subtitle">Sign in to your admin dashboard</p>
            </div>
          </div>

          {/* Error Message */}
          {error && (
            <div className="login-error">
              <div className="login-error-icon">‚ö†Ô∏è</div>
              <span>{error}</span>
            </div>
          )}

          {/* Form */}
          <form onSubmit={handleSubmit} className="login-form">
            <div className="login-field-group">
              <label className="login-label">
                <span className="login-label-text">Username</span>
                <div className="login-input-wrapper">
                  <input
                    type="text"
                    name="username"
                    className="login-input"
                    placeholder={ADMIN_USER}
                    value={form.username}
                    onChange={handleChange}
                    required
                  />
                  <div className="login-input-icon">üë§</div>
                </div>
              </label>
            </div>

            <div className="login-field-group">
              <label className="login-label">
                <span className="login-label-text">Password</span>
                <div className="login-input-wrapper">
                  <input
                    type={showPassword ? "text" : "password"}
                    name="password"
                    className="login-input"
                    placeholder="Enter your password"
                    value={form.password}
                    onChange={handleChange}
                    required
                  />
                  <button
                    type="button"
                    className="login-password-toggle"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                  </button>
                </div>
              </label>
            </div>

            <button 
              type="submit" 
              className={`login-submit ${loading ? 'loading' : ''}`}
              disabled={loading}
            >
              {loading ? (
                <>
                  <div className="login-spinner"></div>
                  Signing in...
                </>
              ) : (
                <>
                  <span>Sign In</span>
                  <div className="login-arrow">‚Üí</div>
                </>
              )}
            </button>
          </form>

          {/* Footer */}
          <div className="login-footer">
            <Link to="/" className="login-back-link">
              <span className="login-back-icon">‚Üê</span>
              Back to Form
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
